import React, {useState, useEffect, useRef} from 'react';
// useEffect to make sure calling API as soon as the page loads
import FlashcardList from './FlashcardList';
import './app.css'
import axios from 'axios'

function App() {
    const [flashcards, setFlashcards] = useState(SAMPLE_FLASHCARDS)
    const [categories, setCategories] = useState([])

    const categoryEl = useRef()
    const amountEl = useRef()
    useEffect(() => {
      axios
        .get('https://opentdb.com/api_category.php')
        .then(res => {
          setCategories(res.data.trivia_categories)
        })
    }, [])

    useEffect(() => {
      axios
        .get('https://opentdb.com/api.php?amount=10')
        .then(res => {
          setFlashcards(res.data.results.map((questionItem,index) => {
            const answer = decodeString(questionItem.correct_answer)
            const options = [...questionItem.incorrect_answers.map(a => decodeString(a)), 
              answer]
            return {
              id: `${index}-${Date.now()}`,
              question: decodeString(questionItem.question),
              answer: answer,
              options: options.sort(() => Math.random() - .5)
            }
          }))
        })
    },[])
    function decodeString(str) {
      const textArea = document.createElement('textarea')
      textArea.innerHTML=str
      return textArea.value
    }
  function handleSubmit(e){
    e.preventDefault();
  }

  return (
    <>
    <form class='header' onSubmit={handleSubmit}>
      <div className='form-group'>
        <label htmlFor='category'>Category</label>
        <select id='category' ref={categoryEl}>
          {categories.map(category => {
            return <option value={category.id} key={category.id}>{category.name}</option>
          })}
        </select>
      </div>
      <div className='form-group'>
        <label htmlFor='amount'>Number Of Qeustions</label>
        <input type='number' id='amount' min='1' step='1' defaultValue={10} ref={amountEl}/>
      </div>
      <div></div>
    </form>
    <div className='container'>
    <FlashcardList flashcards={flashcards} />
    </div>
    </>
  );
}

export default App;

const SAMPLE_FLASHCARDS = [
    {
        id: 1,
        question: "Question 1 ?",
        answer: "4",
        options: [
        '2',
        '3',
        '4',
        '5',]
    },
    {
        id: 2,
        question: "Question 2 ?",
        answer: "Answer",
        options: [
        'Opt 1',
        'Opt 2',
        'Opt 3',
        'Opt 4',]
    }
]
